import React, { Component } from "react";
import Header from "../components/Header";

class About extends Component {
  render() {
    if (this.props.data) {
      var name = this.props.data.name;
      var profilepic = this.props.data.image; //image from linkedin
      var bio = this.props.data.bio;
      var phone = this.props.data.phone;
      var email = this.props.data.email;
      var resumeDownload = "pdfs/" + this.props.data.resumedownload; //need to adjust in json file
      var interests = this.props.data.interests.map(function (interest) {
        return <div key={interest}>{interest}</div>;
      });
    }

    return (
      <section id="about">
        <Header data={this.state.resumeData.main} />
        <div className="row">
          <div className="three columns">
            <img className="profile-pic" src={profilepic} alt="Churchill" />
          </div>
          <div className="nine columns main-col">
            <h2>ABOUT THE ARTIST: ANDREW CHURCHILL</h2>
            <h6>
              “If you obey all the rules, you miss all the fun”. ―Katharine
              Hepburn
            </h6>

            <p>{bio}</p>

            <div className="row">
              <div className="columns download">
                <p>
                  <a href="#contact" className="button">
                    <i className="fa fa-download"></i>Conctact Andrew
                  </a>
                </p>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default About;
